mapper.select:
SELECT * from TB_User_Relation where
userId = #{userId}
and status =1 ;

SELECT * from TB_User_Relation where
userId = #{userId}
and toUserId= #{toUserId} ;

mapper.update:
update TB_User_Relation set
status =
#{status}
where userId = #{userId} and toUserId = #{toUserId};

mapper.delete:
delete from
TB_User_Relation
where userId = #{userId} and toUserId = #{toUserId};
